import React, { Component } from 'react'
import './List.css'

export default class List extends Component {

    /* constructor(props) {
        super(props)
    } */
    state = {
        mouse: true
    }

    todoDone = (event) =>{
        console.log(event.target.checked);
    }

  render() {
      const {mouse} = this.state
    return (
        <div>
            <button onClick={this.todoDone}>完成</button>
            <button>删除</button>
            <div style={{textDecoration:mouse ? 'line-through' : 'none' , backgroundColor:mouse ? 'aqua' : 'none'}} className='list-content'>{this.props.content}</div>
        </div>
    )
  }
}
