        console.log(event.target.value);
    }
    inpValue = (event) =>{
        console.log(event.target.value);
    }

  render() {
    return (
        <div>
            <input type="text" onChange={this.inpValue}/>
            <button onClick={this.handleAdd}>增加</button>
        </div>
    )
  }
}
