import React, { Component } from 'react'
import './index.css'

export default class Item extends Component {

  state = {mouse:false}

  handleMouse = (flag) =>{
    return () =>{
      this.setState({mouse:flag})
    }
  }

  render() {
    const {name, done} = this.props
    return (
      <li style={{backgroundColor:this.state.mouse ? 'blue' : 'white' }} onMouseLeave={this.handleMouse(false)} onMouseEnter={this.handleMouse(true)}>
        <label>
