import React, { Component } from 'react'
import Item from './Item'

const saveTodo = (todos) =>{
    localStorage.todds = JSON.stringify(todos)
}

export default class App extends Component {

    state = {
        inpValue : '',
        todos : []
    }

    //更新时保存到本地
    componentDidUpdate() {
        saveTodo(this.state.todos)
    }

    //挂载时读取
    componentDidMount() {
        const todos = localStorage.todos
        if(!todos) {
            todos = '[]'
        }
        this.setState({
            todos:JSON.parse(todos)
        })
    }

    handelChange = (event) =>{
        console.log(event.target.value);
        this.setState({
            inpValue: event.target.value
        })
    }

    //添加完成的点击事件
    addTodo = () =>{
        const {todos} = this.state
        todos.push({
            content:this.state.inpValue,
            done: false,
            time: Date.now()
        })
        this.setState({
            todos:todos
        })
    }


  render() {
    return (
      <div>
          <div>
            <input type="text" value={this.state.inpValue} onChange={this.handelChange}/>
            <button onClick={this.addTodo}>完成</button>
          </div>
          {
              this.state.todos.map(todo =>(
                <Item key={todo.time} {...todo} addTodo={this.addTodo}/>
              ))
          }
      </div>
    )
  }
}
