//创建外壳
import React, {Component} from "react";
import Item from "./Item";

export default class App extends Component {

    state = {
        inpValue : '',
        todos : []
    }

    //获取input上输入的值
    handelChange = (event) => {
        console.log(event.target.value);
        this.setState({
            inpValue:event.target.value
        })
    }

    //addTodo的方法
    addTodo = () =>{
        const {todos} = this.state
        todos.push({
            content:this.state.inpValue,
            done:false,
            time: Date.now(),
        })
        this.setState({
            todos:todos
        })
        console.log('addTodo被点击了');
    }

    //删除一个todo
    todoDelete = (time) =>{
        const {todos} = this.state
        const newTodo = todos.filter(time =>{
            if(this.state.time !== time) {
                return
            }
        })
        this.setState({
            todos:newTodo
        })
    }


  render() {
    return (
      <div>
          <div>
            <input type="text" value={this.state.inpValue} onChange={this.handelChange}/>
            <button onClick={this.addTodo}>增加</button>
          </div>
            {
                this.state.todos.map((todo) =>(
                    <Item key={todo.time} {...todo} todoDelete={this.todoDelete} todoDone={this.todoDone}/>
                ))
            }
      </div>
    )
  }
}
