import React, { Component } from 'react'
import Item from './Item'


export default class App extends Component {

    state = {
        inpValue: '',
        todos:[],
    }

    //获取输入的值
    handelChange (event) {
        console.log(event.target.value);
        this.setState({
            inpValue:event.target.value
        })
    }
    //添加一个todoList
    addTodo = () =>{
        const {todos} = this.state
        todos.push({
            content:this.props.inpValue,
            done:false,
            time: Date.new()
        })
        this.setState({
            todos:todos
        })
    }

  render() {
    return (
      <div>
          <div>
            <input type="text" onChange={this.handelChange}/>
            <button onClick={this.addTodo}>增加</button>
          </div>
          {
              this.state.todos.map(todo =>(
                <Item {...todo}/>
              ))
          }
      </div>
    )
  }
}
