//创建外壳
import React from "react";
import Footer from "./components/Footer";
import Header from "./components/Header";
import List from "./components/List";
import '../src/App.css'

//创建组件
export default class App extends React.Component{
    state = {todos:[
        {id:'001', name:'吃饭', done:true},
        {id:'002', name:'睡觉', done:true},
        {id:'003', name:'唱歌', done:false},

    ]}

    //添加一个todos
    addTodos = (todoObj) =>{
        //获取原来的todo
        const {todos} = this.state
        //添加一个todo
        const newTodo = [todoObj, ...todos]
        //更新页面
        this.setState({todos:newTodo})
    }

    //开关一个todo对象的状态
    updateDone = (id, done) =>{
        //获取当前状态
        const {todos} = this.state
        //添加数据
        const newTodes = todos.map((todoObj) =>{
            if(todoObj.id === id) return {...todos, done}
            else todoObj
        })
        //更新状态
        this.setState({todos:newTodes})
        
    }

    render() {
        const {todos} = this.state
        return(
            <div className="todo-container">
                <div className="todo-wrap">
                <Header addTodos={this.addTodos}/>
                <List todos={todos} updateDone={this.updateDone}/>
                <Footer/>
                </div>
            </div>
        )
    }
}
