import React, { Component } from 'react'
import './Item.css'

const newTime = (time) =>{
  const d = new Date(time)
  const yarn = d.getFullYear()
  const month = d.getMonth() + 1
  const data = d.getDate()
  const hour = d.getHours()
  const minute = d.getMinutes()
  const second = d.getSeconds()
  return `${yarn}-${month}-${data}  ${hour}:${minute}:${second}`

}

export default class Item extends Component {

  //删除一个todo的回调函数
  todoDelete = () =>{
    this.props.deleteTodo(this.props.time)
  }

  todoDone = () =>{
    this.props.doneTodo(this.props.time)
  }

  render() {
    let classNameTodo = 'todo-content'
    if(this.props.done) {
      classNameTodo = classNameTodo + ' ' + 'done'
    }
    const todoTime = newTime(this.props.time)
    return (
      <div>
        <button onClick={this.todoDone}>完成</button>
        <button onClick={this.todoDelete}>删除</button>
        <div className='todo-time'>{todoTime}</div>
        <div className={classNameTodo}>{this.props.content}</div>
      </div>
    )
  }
}
