import React, { Component } from 'react'
import './index.css'

export default class Item extends Component {

    state = {mouse:false}
    //鼠标滑过高亮效果
    mouseLeaveDone = (flag) =>{
        return () =>{
            this.setState({mouse:flag})
        } 
    }

    //开关一个状态的回调
    updateTodoDone = (id) =>{
        return (event) =>{
            console.log(id, event.target.checked);
        }
    }
  render() {
    //把id的属性拿过来
    const {id, name, done} = this.props
    const {mouse} = this.state
    return (
        <li style={{backgroundColor:mouse ? '#00CED1' : 'white'}} onMouseLeave={this.mouseLeaveDone(false)} onMouseEnter={this.mouseLeaveDone(true)}>
            <label>
                <input type="checkbox" defaultChecked={done} onChange={this.updateTodoDone(id)}/>
                <span>{name}</span>
            </label>
            <button className="btn btn-danger" style={{display:mouse ? 'block' : 'none'}}>删除</button>
        </li>
    )
  }
}
