//创建外壳
import React,{Component} from "react";
import {nanoid} from 'nanoid'
import List from "./List";
import './App.css'

//存入数组，序列化
const saveTodo = function(todos) {
    localStorage.todos = JSON.stringify(todos)
}

export default class Todo extends Component {

    //初始化状态
    state = {
        inpValue:'',
        todos:[],
    }

    //保存本地
    componentDidUpdate() {
        saveTodo(this.state.todos)
        console.log('todo写入');
    }
    
    //读取数据
    componentDidMount() {
        let todos = localStorage.todos
        if(!todos) {
            todos = '[]'
        }
        this.setState({
            todos: JSON.parse(todos)
        })
        console.log('todo读出');
    }

    //input的事件监听，获取到输入的值
    handelTodo = (event) =>{
        if(event.target.value === "") {
            alert('输入不能为空')
            return
        }
        this.setState({
            inpValue:event.target.value
        })
    }

    //button的点击事件,添加一个todoList
    addTodo = () =>{
        const {todos} = this.state
        if(this.state.inpValue.trim() === "") {
            alert('输入不能为空')
            return
        }
        //把输入的内容添加进去
        todos.push({
            content: this.state.inpValue,
            done: false,
            time:Date.now()
        })
        //输入完之后输入框变为空
        this.state.inpValue = ''
        //更新状态渲染到页面
        this.setState({
            todos:todos,
        })
    }

    //删除一个todoList
    deleteTodo = (time) =>{
        const {todos} = this.state
        //过滤一下todos数组,如果和传进来的一样就过滤掉传进来的time
        const newTodo = todos.filter(todo => todo.time !== time)
        this.setState({todos:newTodo})
    }

    //增加一个done
    doneTodo = (time) =>{
        const newTodo = [...this.state.todos]
        newTodo.forEach(todo =>{
            console.log('donedonedone');
            if(todo.time === time) {
                if (todo.done === false) {
                    todo.done = true
                } else {
                    todo.done = false
                }
            }
        })
        this.setState({todos:newTodo})
        }
    
    render() {
        return(
            <div className="todo-cell">
                <div>
                    <input type="text" value={this.state.inpValue} onChange={this.handelTodo}/>
                    <button onClick={this.addTodo}>增加</button>
                </div>
                    {
                        //遍历数组传给List
                        this.state.todos.map((todo) =>(
                            <List key={nanoid()} {...todo} deleteTodo={this.deleteTodo} doneTodo={this.doneTodo}/>
                        ))
                    }
            </div>
        )
    }
}
