    handleAdd = (inpValue) =>{
        console.log(inpValue.target.checked);
    }

  render() {
    return (
        <div>
            <input type="text" defaultValue={this.inpValue}/>
            <button onClick={this.handleAdd}>增加</button>
        </div>
    )
  }
}
