//创建外壳
import React,{Component} from "react";
import List from "./List";
import './App.css'


export default class Todo extends Component {

    //初始化状态
    state = {
        inpValue:'',
        todos:[],
    }

    //button的点击事件
    addTodo = () =>{
        const todos = [...this.state.todos]
        todos.push({
            content: this.state.inpValue,
            done: false,
            time:new Date().getTime()
        })
        this.setState({
            todos:todos,
        })
    }

    //input的事件监听
    // updateTodo = (event) =>{
    //     this.setState({
    //         inpValue:event.target.value
    //     })
    // }
    
    render() {
        return(
            <div className="todo-cell">
                <div>
                    <input type="text" value={this.state.inpValue} onChange={this.updateTodo}/>
                    <button onClick={this.addTodo}>增加</button>
                </div>
                    {
                        //遍历数组展穿给List
                        this.state.todos.map((todo) =>(
                            <List {...todo}/>
                        ))
                    }
            </div>
        )
    }
}
