import React, { Component } from 'react'
import './Item.css'


export default class Item extends Component {

    //删除一个todo的回调函数
    deleTodo = () =>{
        this.props.todoDelete(this.props.time)
    }

   /*  //增加一个done的回调函数
    doneTodo = () =>{
        console.log('21232');
    } */


  render() {
    return (
        <div>
            <button>完成</button>
            <button deleTodo={this.deleTodo}>删除</button>
            <div className='Item-content'>{this.props.content}</div>
        </div>
    )
  }
}
