import React, { Component } from 'react'
import './Item.css'

const newTime = (time) =>{
    const d = new Date(time)
    const yarn = d.getFullYear()
    const month = d.getMonth()
    const data = d.getDate()
    const hours = d.getHours()
    const minute = d.getMinutes()
    const second = d.getSeconds()
    return yarn + '-' + month + '-' + data + ' ' + hours + ':' + minute + ':' + second
}

export default class Item extends Component {

    delTodo = () =>{
        this.props.deleteTodo(this.props.time)
    }

    donTodo = () =>{
        this.props.doneTodo(this.props.time)
    }

  render() {
      let classNameTodo = 'todo-content'
      if(this.props.done) {
        classNameTodo = classNameTodo + ' ' + 'done'
      }

      const time = newTime(this.props.time)
    return (
      <div>
          <button onClick={this.donTodo}>完成</button>
          <button onClick={this.delTodo}>删除</button>
          <div className='todo-time'>{time}</div>
          <div className={classNameTodo}>{this.props.content}</div>
      </div>
    )
  }
}
