//创建外壳
import React, {Component} from "react";
import Item from "./Item";

export default class App extends Component {

    state = {
        inpValue : '',
    }

    handelChange (event) {
        console.log(event.target.value);
        this.setState({
            inpValue:event.target.value
        })
    }

  render() {
    return (
      <div>
          <div>
          <input type="text" onChange={this.handelChange}/>
          <button>增加</button>
          </div>
          <Item/>
      </div>
    )
  }
}
