import React, { Component } from 'react'
import Item from './Item'

const saveTodo = (todos) =>{
    localStorage.todos = JSON.stringify(todos)
}

export default class App extends Component {

    //初始化
    state = {
        inpValue: '',
        todos:[]
    }

    componentDidUpdate() {
        saveTodo(this.state.todos)
    }

    componentDidMount() {
        const {todos} = this.state
        todos = '[]'
        this.setState({
            todos:JSON.parse(todos)
        })
    }

    //获取到input输入的值
    handelChange = (event) =>{
        console.log(event.target.value);
        this.setState({
            inpValue:event.target.value
        })
    }

    //增加一个todo
    addTodo = () =>{
        console.log('按钮被点击了');
        const {todos} = this.state
        todos.push({
            content:this.state.inpValue,
            done:false,
            time:Date.now()
        })
        this.setState({
            todos:todos
        })
    }


    //删除一个todos
    deleteTodo = (time) =>{
        const {todos} = this.state
        const newTodo = todos.filter(todo => todo.time !== time)
        this.setState({
            todos:newTodo
        })
    }

    //给一todo开关一个class
    doneTodo = (time) =>{
        const newTodo = [...this.state.todos]
        newTodo.forEach(todo =>{
            if(todo.time === time) {
                if(todo.done === false) {
                    todo.done = true
                } else {
                    todo.done = false
                }
            }
        })
        this.setState({
            todos:newTodo
        })
    }
    


  render() {
    return (
      <div>
          <div>
              <input type="text" value={this.state.inpValue} onChange={this.handelChange}/>
              <button onClick={this.addTodo}>完成</button>
          </div>
          {
              this.state.todos.map(todo =>(
                <Item key={todo.time} {...todo} deleteTodo={this.deleteTodo} doneTodo={this.doneTodo}/>
              ))
          }
      </div>
    )
  }
}
