import React, { Component } from 'react'
import './index.css'

export default class Item extends Component {
  render() {

    //鼠标滑过
    mouseLeaveDone = () =>{
        console.log('true')
    }
    //鼠标离开
    mouseEnterDone = () =>{
        console.log('false');
    }
    //把id的属性拿过来
    const {id, name, done} = this.props
    return (
        <li onMouseLeave={this.mouseLeaveDone(true)} onMouseEnter={this.mouseEnterDone(false)}>
            <label>
                <input type="checkbox" defaultChecked={done}/>
                <span>{name}</span>
            </label>
            <button className="btn btn-danger" style={{display:'none'}}>删除</button>
        </li>
    )
  }
}
