import React, { Component } from 'react'
import './Item.css'

const todoTime = (time) =>{
    const d = new Date(time)
    const yarn = d.getFullYear();
    const month = d.getMonth() + 1;
    const data = d.getDate();
    const hours = d.getHours();
    const minute = d.getMinutes();
    const second = d.getSeconds();
    return yarn + '-' + month + '-' + data + ' ' + hours + ':' + minute + ':' + second
}


export default class Item extends Component {

    //删除一个todo的回调函数
    deleTodo = () =>{
        this.props.todoDelete(this.props.time)
    }

    //增加一个done的回调函数
    doneTodo = () =>{
        this.props.todoDone(this.props.time)
    }


  render() {
      let todoClassName = 'Item-content'
      if(this.props.done) {
          todoClassName = todoClassName + ' ' + 'done'
          console.log(todoClassName);
      }
    return (
        <div>
            <button onClick={this.doneTodo}>完成</button>
            <button onClick={this.deleTodo}>删除</button>
            <div>{this.todoTime}</div>
            <div className={todoClassName}>{this.props.content}</div>
        </div>
    )
  }
}
