export default class Todo extends Component {

    state = {
        inpValue:'',
        todos:[]
    }

    //button的点击事件
    addTodo = () =>{
        const todos = {...this.state.inpValue}
        todos.push({
            contnet: this.state.inpValue,
            done: false,
            time:new Date().getTime()
        })
    }

    //input的事件监听
    updateTodo = (event) =>{
        this.setState({
            inpValue:event.target.value
        })
    }
    
