//创建外壳
import React,{Component} from "react";
//引入css文件
import './App.css'

export default class Todo extends Component {
    render() {
        return(
            <div className="todo-cell">
                <div>
                    <input type="text"  value={''}/>
                    <button>Add</button>
                </div>
                <div>
                    <div className="todo-div">111</div>
                    <button>完成</button>
                    <button>删除</button>
                </div>
            </div>
        )
    }
}
