import React, { Component } from 'react'
import Item from './Item'

const saveTodo = (todos) =>{
    localStorage.todos = JSON.stringify(todos)
}

export default class App extends Component {

    state = {
        inpValue: '',
        todos:[],
    }

    componentDidUpdate() {
        saveTodo(this.state.todos)
    }

    componentDidMount() {
        
        this.setState({
            todos:JSON.parse(this.state.todos)
        })
    }
    //获取输入的值
    handelChange = (event) => {
        this.setState({
            inpValue:event.target.value
        })
    }
    //添加一个todoList
    addTodo = () =>{
        const {todos} = this.state
        todos.push({
            content:this.state.inpValue,
            done:false,
            time: Date.now()
        })
        this.setState({
            todos:todos
        })
    }

    //删除一个tood
    deleteTodo = (time) =>{
        const {todos} = this.state
        const newTodo = todos.filter(todo =>{
            if(todo.time !== time) {
                return todos
            }
        })
        this.setState({
            todos:newTodo
        })
    }

    //增加一个done
    doneTodo = (time) => {
        const newTodo = [...this.state.todos]
        newTodo.forEach(todo => {
            if(todo.time === time) {
                if(todo.done == false) {
                    todo.done = true
                } else {
                    todo.done = false
                }
            }
        })
        this.setState({
            todos:newTodo
        })
    }

  render() {
    return (
      <div>
          <div>
            <input type="text" value={this.state.inpValue} onChange={this.handelChange}/>
            <button onClick={this.addTodo}>增加</button>
          </div>
          {
              this.state.todos.map((todo) =>(
                <Item key={todo.time} {...todo} addTodo={this.addTodo} deleteTodo={this.deleteTodo} doneTodo={this.doneTodo}/>
              ))
          }
      </div>
    )
  }
}
