import React, { Component } from 'react'
import Item from './Item'

export default class App extends Component {


    state = {
        inpValue: '',
        todos: []
    }

    //获取到当前输入的值
    handelChange = (event) =>{
        console.log(event.target.value);
        this.setState({
            inpValue:event.target.value
        })
    }

    //增加一个todo
    addTodo = () =>{
        const {todos} = this.state
        todos.push({
            content: this.state.inpValue,
            done: false,
            time: Date.now()
        })
        this.setState({
            todos:todos
        })
    }

    //删除一个todo
    deleteTodo = (time) =>{
        const newTodo = this.state.todos
        newTodo.filter(todo =>{
            if(todo.time !== time) {
                newTodo
            }
        })
        this.setState({
            todos:newTodo
        })
    }

  render() {
    return (
      <div>
          <div>
              <input type="text" value={this.state.inpValue} onChange={this.handelChange}/>
              <button onClick={this.addTodo}>增加</button>
          </div>
          {
              this.state.todos.map((todo) =>(
                <Item key={todo.time} {...todo} deleteTodo={this.deleteTodo}/>
              ))
          }
      </div>
    )
  }
}
