import React, { Component } from 'react'
import './List.css'

export default class List extends Component {

    /* constructor(props) {
        super(props)
    } */
    state = {
        mouse: false
    }

    //开关一个todoDone的回调
    todoDone = () =>{
        this.props.doneTodo(this.props.time)

    }

    //删除一个todoList的回调
    todoDelete = () =>{
        this.props.deleteTodo(this.props.time)
    }

  render() {

      let todoClassName = 'list-content'
      if(this.props.done) {
          todoClassName = todoClassName + ' ' + 'done'
      }

      const updateTime = newTime(this.props.time)
    return (
        <div>
            <button onClick={this.todoDone}>完成</button>
            <button onClick={this.todoDelete}>删除</button>
            <div className={todoClassName}>{this.props.content}</div>
            <div>{updateTime}</div>
        </div>
    )
  }
}
