import React, { Component } from 'react'
import './Item.css'

export default class Item extends Component {

    deleTodo = () =>{
        this.props.deleteTodo(this.props.time)
        console.log('qqqqqqqqqqqqq');
    }

    doneTodo = () =>{
        this.props.todoDone(this.props.time)
    }
  render() {
      let classNameTodo = 'item-content'
      if(this.props.done) {
          classNameTodo = classNameTodo + ' ' + 'done'
      }
    return (
      <div>
          <button onClick={this.doneTodo}>完成</button>
          <button onClick={this.deleTodo}>删除</button>
          <div className={classNameTodo}>{this.props.content}</div>
      </div>
    )
  }
}
