//创建外壳
import React,{Component} from "react";
import {nanoid} from 'nanoid'
import List from "./List";
import { ajax } from "./utils/request";
import './App.css'

//存入数组，序列化
const saveTodo = function(todos) {
    localStorage.todos = JSON.stringify(todos)
}

export default class Todo extends Component {

    //初始化状态
    state = {
        inpValue:'',
        todos:[],
    }

    //保存本地
    componentDidUpdate() {
        saveTodo(this.state.todos)
        console.log('todo写入');
    }

    initTodo () {
        ajax('GET', 'http://localhost:3005/todo/all', null, (response) =>{
            console.log('response', response);
            this.setState({
                todos:response.data.map(item =>({
                    ...item,
                    content: item.task
                }))
            })
        })
    }
    
    //读取数据
    componentDidMount() {
        this.initTodo()
    }

    //input的事件监听，获取到输入的值
    handelTodo = (event) =>{
        this.setState({
            inpValue:event.target.value
        })
    }

    //button的点击事件,添加一个todoList
    addTodo = () =>{
        ajax('POST', 'http://localhost:3005/todo/', {
            task: this.state.inpValue,
        }, ()=>{
            this.initTodo()
        })
    }

    //删除一个todoList
    deleteTodo = (id) =>{
        ajax('GET', 'http://localhost:3005/todo/delete/' + id, null, () =>{
            this.initTodo()
        })
    }

    //增加一个done
    doneTodo = (opts) =>{
        ajax('POST', 'http://localhost:3005/todo/update/' + opts.id, {
            task: opts.content,
            done: !opts.done,
            id: opts.id
        }, ()=>{
            this.initTodo()
        })
    }
    
    render() {
        return(
            <div className="todo-cell">
                <div>
                    <input type="text" value={this.state.inpValue} onChange={this.handelTodo}/>
                    <button onClick={this.addTodo}>增加</button>
                </div>
                    {
                        //遍历数组传给List
                        this.state.todos.map((todo) =>(
                            <List id={todo.id} done={todo.done} key={nanoid()} {...todo} deleteTodo={this.deleteTodo} doneTodo={this.doneTodo}/>
                        ))
                    }
            </div>
        )
    }
}
