import React, { Component } from 'react'
import './Item.css'

export default class Item extends Component {

  //删除一个todo的回调函数
  todoDelete = () =>{
    this.props.deleteTodo(this.props.time)
  }

  render() {
    return (
      <div>
        <button>完成</button>
        <button onClick={this.todoDelete}>删除</button>
        <div className='todo-content'>{this.props.content}</div>
      </div>
    )
  }
}
