//创建外壳
import React,{Component} from "react";
import Header from "./Header";
import List from "./List";
import './App.css'


export default class Todo extends Component {

    //初始化状态
    state = {todos:[
        {id:'001', name:'吃瓜', done:true}
    ]}

    render() {
        const {todos} = this.state
        return(
            <div className="todo-cell">
                <Header/>
                <List todos={todos}/>
            </div>
        )
    }
}
