import React, { Component } from 'react'
import Item from './Item'

const saveTodos = (todos) =>{
    localStorage.todos = JSON.stringify(todos)
}

export default class App extends Component {


    state = {
        inpValue: '',
        todos: []
    }

    componentDidUpdate() {
        saveTodos(this.state.todos)
    }

    componentDidMount() {
        const todos = localStorage.todos
        if(!newTodo) {
            this.setState({
                newTodo:JSON.parse(todos)
            })
        }
    }

    //获取到当前输入的值
    handelChange = (event) =>{
        console.log(event.target.value);
        this.setState({
            inpValue:event.target.value
        })
    }

    //增加一个todo
    addTodo = () =>{
        const {todos} = this.state
        todos.push({
            content: this.state.inpValue,
            done: false,
            time: Date.now()
        })
        this.setState({
            todos:todos
        })
    }

    //添加一个done
    todoDone = (time) =>{
        const newTodo = this.state.todos
        newTodo.forEach(todo =>{
            if(todo.time === time) {
                if(todo.done === false) {
                    todo.done = true
                } else {
                    todo.done = false
                }
            }
        })
        this.setState({
            todos:newTodo
        })
    }

    //删除一个todo
    deleteTodo = (time) =>{
        const {todos} = this.state
        const newTodo = todos.filter(todo => todo.time !== time)
        console.log(newTodo);
        this.setState({
            todos:newTodo
        })
    }

  render() {
    return (
      <div>
          <div>
              <input type="text" value={this.state.inpValue} onChange={this.handelChange}/>
              <button onClick={this.addTodo}>增加</button>
          </div>
          {
              this.state.todos.map((todo) =>(
                <Item key={todo.time} {...todo} deleteTodo={this.deleteTodo} todoDone={this.todoDone}/>
              ))
          }
      </div>
    )
  }
}
